class Student:
    def __init__(self, name, score):
        self.name = name
        self.score = score

    def show_info(self):
        print(f'{self.name} 的分數是 {self.score}')

s1 = Student('Alice', 90)
s2 = Student('Bob', 80)
s1.show_info()
s2.show_info()

class Animal:
    def make_sound(self):
        print('動物發出聲音')

class Dog(Animal):
    def make_sound(self):
        print('汪汪！')

class Cat(Animal):
    def make_sound(self):
        print('喵～')

d = Dog()
c = Cat()
d.make_sound()
c.make_sound()
