import time

def measure_time(func, *args):
    start = time.time()
    func(*args)
    end = time.time()
    return round(end - start, 3)

# 測試
import time

def slow_add(a, b):
    time.sleep(2)
    return a + b

print(measure_time(slow_add, 3, 5))  # 約 2.0

# 模組區塊
def c_to_f(c):
    return c * 9 / 5 + 32

def f_to_c(f):
    return (f - 32) * 5 / 9

# 主程式區塊
type_ = input().strip().upper()  # 讀取轉換方向 (C 或 F)
value = float(input())            # 讀取溫度值

if type_ == 'C':
    print(c_to_f(value))
elif type_ == 'F':
    print(f_to_c(value))
else:
    print('輸入錯誤，請輸入 C 或 F')
