from collections import Counter

class Solution:
    def isPossibleDivide(self, nums, k):
        if len(nums) % k != 0:
            return False

        count = Counter(nums)
        for num in sorted(count):
            if count[num] > 0:
                need = count[num]
                for i in range(num, num + k):
                    if count[i] < need:
                        return False
                    count[i] -= need
        return True
