class Solution:
    def kWeakestRows(self, mat, k):
        def count_soldiers(row):
            left, right = 0, len(row)
            while left < right:
                mid = (left + right) // 2
                if row[mid] == 1:
                    left = mid + 1
                else:
                    right = mid
            return left

        soldiers = [(count_soldiers(row), i) for i, row in enumerate(mat)]
        soldiers.sort()
        return [i for _, i in soldiers[:k]]
