class Solution:
    def evalRPN(self, tokens):
        stack = []
        for token in tokens:
            if token in '+-*/':
                b = stack.pop()
                a = stack.pop()
                if token == '+':
                    stack.append(a + b)
                elif token == '-':
                    stack.append(a - b)
                elif token == '*':
                    stack.append(a * b)
                elif token == '/':
                    # 向零取整
                    stack.append(int(a / b) if a * b >= 0 else - (abs(a) // abs(b)))
            else:
                stack.append(int(token))
        return stack[0]
