class Solution:
    def shortestPalindrome(self, s):
        if not s:
            return ""
        temp = s + '#' + s[::-1]
        n = len(temp)
        lps = [0] * n
        for i in range(1, n):
            length = lps[i-1]
            while length > 0 and temp[i] != temp[length]:
                length = lps[length-1]
            if temp[i] == temp[length]:
                length += 1
            lps[i] = length
        to_add = s[lps[-1]:][::-1]
        return to_add + s
