class Solution:
    def calculate(self, s):
        stack = []
        num = 0
        sign = '+'
        s += '+'  # 方便最後處理最後一個數字

        for c in s:
            if c.isdigit():
                num = num * 10 + int(c)
            elif c in '+-*/':
                if sign == '+':
                    stack.append(num)
                elif sign == '-':
                    stack.append(-num)
                elif sign == '*':
                    stack.append(stack.pop() * num)
                elif sign == '/':
                    top = stack.pop()
                    if top < 0:
                        stack.append(-(-top // num))
                    else:
                        stack.append(top // num)
                sign = c
                num = 0

        return sum(stack)
