class Solution:
    def summaryRanges(self, nums):
        if not nums:
            return []
        res = []
        start = end = nums[0]
        for n in nums[1:]:
            if n == end + 1:
                end = n
            else:
                if start == end:
                    res.append(str(start))
                else:
                    res.append("{}->{}".format(start, end))
                start = end = n
        if start == end:
            res.append(str(start))
        else:
            res.append("{}->{}".format(start, end))
        return res
