class Solution:
    def countDigitOne(self, n):
        if n <= 0:
            return 0
        count = 0
        i = 1  # 當前位數 1、10、100...
        while i <= n:
            divider = i * 10
            count += (n // divider) * i + min(max(n % divider - i + 1, 0), i)
            i *= 10
        return count
