class Solution:
    def singleNumber(self, nums):
        xor = 0
        for num in nums:
            xor ^= num

        # 找到 xor 的最右邊 1
        diff_bit = xor & -xor

        a = b = 0
        for num in nums:
            if num & diff_bit:
                a ^= num
            else:
                b ^= num
        return [a, b]
