class Solution:
    def numberToWords(self, num):
        if num == 0:
            return "Zero"

        def one(num):
            switcher = ["", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine"]
            return switcher[num]

        def two_less_20(num):
            switcher = ["Ten", "Eleven", "Twelve", "Thirteen", "Fourteen", "Fifteen",
                        "Sixteen", "Seventeen", "Eighteen", "Nineteen"]
            return switcher[num - 10]

        def ten(num):
            switcher = ["", "", "Twenty", "Thirty", "Forty", "Fifty", "Sixty", "Seventy", "Eighty", "Ninety"]
            return switcher[num]

        def two(num):
            if num == 0:
                return ""
            elif num < 10:
                return one(num)
            elif num < 20:
                return two_less_20(num)
            else:
                tens = num // 10
                rest = num % 10
                return ten(tens) + (" " + one(rest) if rest != 0 else "")

        def three(num):
            hundred = num // 100
            rest = num % 100
            if hundred != 0 and rest != 0:
                return one(hundred) + " Hundred " + two(rest)
            elif hundred == 0 and rest != 0:
                return two(rest)
            elif hundred != 0 and rest == 0:
                return one(hundred) + " Hundred"
            else:
                return ""

        billion = num // 1000000000
        million = (num - billion * 1000000000) // 1000000
        thousand = (num - billion * 1000000000 - million * 1000000) // 1000
        rest = num % 1000

        result = ""
        if billion != 0:
            result += three(billion) + " Billion"
        if million != 0:
            result += " " if result != "" else ""
            result += three(million) + " Million"
        if thousand != 0:
            result += " " if result != "" else ""
            result += three(thousand) + " Thousand"
        if rest != 0:
            result += " " if result != "" else ""
            result += three(rest)
        return result
