import math

class Solution:
    def numSquares(self, n):
        # dp[i] 表示數字 i 最少需要的完全平方數個數
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        for i in range(1, n + 1):
            j = 1
            while j * j <= i:
                dp[i] = min(dp[i], dp[i - j * j] + 1)
                j += 1

        return dp[n]
