class Solution:
    def moveZeroes(self, nums):
        """
        Do not return anything, modify nums in-place instead.
        """
        zero_pos = 0  # 指向下一個應該放非零數字的位置
        for i in range(len(nums)):
            if nums[i] != 0:
                nums[zero_pos], nums[i] = nums[i], nums[zero_pos]
                zero_pos += 1
