class Solution:
    def maxProfit(self, prices):
        if not prices:
            return 0

        n = len(prices)
        hold = -prices[0]    # 持有股票的最大收益
        sold = 0             # 剛賣出股票的最大收益
        rest = 0             # 不持有股票且未剛賣出的最大收益

        for price in prices[1:]:
            prev_sold = sold
            sold = hold + price          # 今天賣出
            hold = max(hold, rest - price)  # 今天持有或買入
            rest = max(rest, prev_sold)  # 今天休息

        return max(sold, rest)
