import heapq

class Solution:
    def nthSuperUglyNumber(self, n, primes):
        ugly = [1]
        indices = [0] * len(primes)
        vals = list(primes)

        for _ in range(1, n):
            next_ugly = min(vals)
            ugly.append(next_ugly)

            for i in range(len(primes)):
                if vals[i] == next_ugly:
                    indices[i] += 1
                    vals[i] = ugly[indices[i]] * primes[i]

        return ugly[-1]
