class Solution:
    def maxEnvelopes(self, envelopes):
        if not envelopes:
            return 0
        # 先按寬度升序，如果寬度相同，按高度降序
        envelopes.sort(key=lambda x: (x[0], -x[1]))
        import bisect
        dp = []
        for _, h in envelopes:
            idx = bisect.bisect_left(dp, h)
            if idx == len(dp):
                dp.append(h)
            else:
                dp[idx] = h
        return len(dp)
