import heapq
from collections import defaultdict

class Twitter:
    def __init__(self):
        self.time = 0
        self.tweets = defaultdict(list)
        self.following = defaultdict(set)

    def postTweet(self, userId, tweetId):
        self.time += 1
        self.tweets[userId].append((self.time, tweetId))
        if userId not in self.following[userId]:
            self.following[userId].add(userId)

    def getNewsFeed(self, userId):
        res = []
        if userId not in self.following:
            return res

        heap = []
        for fid in self.following[userId]:
            tws = self.tweets.get(fid)
            if tws:
                idx = len(tws) - 1
                ttime, tid = tws[idx]
                heapq.heappush(heap, (-ttime, tid, fid, idx))

        while heap and len(res) < 10:
            negtime, tid, fid, idx = heapq.heappop(heap)
            res.append(tid)
            if idx - 1 >= 0:
                ttime, tid_prev = self.tweets[fid][idx - 1]
                heapq.heappush(heap, (-ttime, tid_prev, fid, idx - 1))

        return res

    def follow(self, followerId, followeeId):
        if followerId == followeeId:
            self.following[followerId].add(followerId)
            return
        self.following[followerId].add(followeeId)
        if followerId not in self.following[followerId]:
            self.following[followerId].add(followerId)

    def unfollow(self, followerId, followeeId):
        if followerId == followeeId:
            return
        if followeeId in self.following.get(followerId, set()):
            self.following[followerId].remove(followeeId)
