class Solution:
    def countNumbersWithUniqueDigits(self, n):
        if n == 0:
            return 1
        n = min(n, 10)  # 超過10位數會重複
        res = 10
        uniqueDigits = 9
        availableNumber = 9
        for i in range(2, n + 1):
            uniqueDigits *= availableNumber
            res += uniqueDigits
            availableNumber -= 1
        return res
