import collections
import random

class RandomizedCollection:

    def __init__(self):
        self.data = []
        self.idx_map = collections.defaultdict(set)

    def insert(self, val):
        self.data.append(val)
        self.idx_map[val].add(len(self.data) - 1)
        return len(self.idx_map[val]) == 1

    def remove(self, val):
        if not self.idx_map[val]:
            return False
        remove_idx = self.idx_map[val].pop()
        last_val = self.data[-1]
        self.data[remove_idx] = last_val
        self.idx_map[last_val].add(remove_idx)
        self.idx_map[last_val].discard(len(self.data) - 1)
        self.data.pop()
        return True

    def getRandom(self):
        return random.choice(self.data)
