class Solution:
    def validUtf8(self, data):
        n_bytes = 0

        for num in data:
            bin_str = format(num, '#010b')[-8:]
            if n_bytes == 0:
                for bit in bin_str:
                    if bit == '0':
                        break
                    n_bytes += 1
                if n_bytes == 0:
                    continue
                if n_bytes == 1 or n_bytes > 4:
                    return False
                n_bytes -= 1
            else:
                if not (bin_str[0] == '1' and bin_str[1] == '0'):
                    return False
                n_bytes -= 1

        return n_bytes == 0
