class Solution:
    def maxRotateFunction(self, A):
        n = len(A)
        if n == 0:
            return 0
        total = sum(A)
        F = sum(i * num for i, num in enumerate(A))
        max_val = F
        for i in range(n-1, 0, -1):
            F = F + total - n * A[i]
            max_val = max(max_val, F)
        return max_val
