class Solution:
    def findNthDigit(self, n):
        length = 1  # digit length
        count = 9   # count of numbers with this digit length
        start = 1   # starting number of this digit length

        while n > length * count:
            n -= length * count
            length += 1
            count *= 10
            start *= 10

        start += (n - 1) // length
        s = str(start)
        return int(s[(n - 1) % length])
