class Solution:
    def canCross(self, stones):
        stone_set = set(stones)
        last_stone = stones[-1]
        jumps = {stone: set() for stone in stones}
        jumps[0].add(0)

        for stone in stones:
            for k in jumps[stone]:
                for step in [k - 1, k, k + 1]:
                    if step > 0 and stone + step in stone_set:
                        jumps[stone + step].add(step)

        return bool(jumps[last_stone])
