class Solution:
    def toHex(self, num):
        if num == 0:
            return "0"
        hex_chars = "0123456789abcdef"
        if num < 0:
            num += 2**32
        res = ""
        while num > 0:
            res = hex_chars[num & 15] + res
            num >>= 4
        return res
