class Solution:
    def thirdMax(self, nums):
        first = second = third = float('-inf')
        nums_set = set(nums)  # 去重
        for num in nums_set:
            if num > first:
                third = second
                second = first
                first = num
            elif num > second:
                third = second
                second = num
            elif num > third:
                third = num
        return third if len(nums_set) >= 3 else first
