class Solution:
    def strongPasswordChecker(self, s):
        n = len(s)
        has_lower = has_upper = has_digit = 0
        for c in s:
            if c.islower(): has_lower = 1
            elif c.isupper(): has_upper = 1
            elif c.isdigit(): has_digit = 1
        missing_types = 3 - (has_lower + has_upper + has_digit)

        replace = 0
        one_mod = two_mod = 0
        i = 2
        while i < n:
            if s[i] == s[i-1] == s[i-2]:
                length = 2
                while i < n and s[i] == s[i-1]:
                    length += 1
                    i += 1
                replace += length // 3
                if length % 3 == 0: one_mod += 1
                elif length % 3 == 1: two_mod += 1
            else:
                i += 1

        if n < 6:
            return max(6 - n, missing_types)
        elif n <= 20:
            return max(replace, missing_types)
        else:
            delete = n - 20
            # 優先刪除 mod 3 == 0
            reduce = min(delete, one_mod)
            replace -= reduce
            delete -= reduce

            # 再刪除 mod 3 == 1
            reduce = min(max(delete, 0), two_mod * 2) // 2
            replace -= reduce
            delete -= reduce * 2

            # 最後刪除其他
            replace -= max(delete, 0) // 3

            return (n - 20) + max(missing_types, replace)
