class Node:
    def __init__(self):
        self.keys = set()
        self.prev = None
        self.next = None
        self.count = 0

class AllOne:
    def __init__(self):
        self.head = Node()
        self.tail = Node()
        self.head.next = self.tail
        self.tail.prev = self.head
        self.key_to_node = {}

    def _insert_after(self, prev_node, count):
        node = Node()
        node.count = count
        node.prev = prev_node
        node.next = prev_node.next
        prev_node.next.prev = node
        prev_node.next = node
        return node

    def _remove(self, node):
        node.prev.next = node.next
        node.next.prev = node.prev

    def inc(self, key):
        if key in self.key_to_node:
            node = self.key_to_node[key]
            next_count = node.count + 1
            if node.next == self.tail or node.next.count > next_count:
                new_node = self._insert_after(node, next_count)
            else:
                new_node = node.next
            new_node.keys.add(key)
            self.key_to_node[key] = new_node
            node.keys.remove(key)
            if not node.keys:
                self._remove(node)
        else:
            if self.head.next == self.tail or self.head.next.count > 1:
                new_node = self._insert_after(self.head, 1)
            else:
                new_node = self.head.next
            new_node.keys.add(key)
            self.key_to_node[key] = new_node

    def dec(self, key):
        if key not in self.key_to_node:
            return
        node = self.key_to_node[key]
        prev_count = node.count - 1
        node.keys.remove(key)
        if prev_count == 0:
            del self.key_to_node[key]
        else:
            if node.prev == self.head or node.prev.count < prev_count:
                new_node = self._insert_after(node.prev, prev_count)
            else:
                new_node = node.prev
            new_node.keys.add(key)
            self.key_to_node[key] = new_node
        if not node.keys:
            self._remove(node)

    def getMaxKey(self):
        if self.tail.prev == self.head:
            return ""
        return next(iter(self.tail.prev.keys))

    def getMinKey(self):
        if self.head.next == self.tail:
            return ""
        return next(iter(self.head.next.keys))
