class Solution:
    def eraseOverlapIntervals(self, intervals):
        if not intervals:
            return 0

        # 先按照結束時間排序
        intervals.sort(key=lambda x: x[1])
        end = intervals[0][1]
        count = 1  # 記錄不重疊區間數

        for i in range(1, len(intervals)):
            if intervals[i][0] >= end:
                end = intervals[i][1]
                count += 1

        # 需要移除的區間數 = 總區間數 - 不重疊區間數
        return len(intervals) - count
