from collections import Counter

class Solution:
    def findAnagrams(self, s, p):
        ns, np = len(s), len(p)
        if ns < np:
            return []

        p_count = Counter(p)
        s_count = Counter(s[:np])
        res = []

        if s_count == p_count:
            res.append(0)

        for i in range(1, ns - np + 1):
            s_count[s[i - 1]] -= 1
            if s_count[s[i - 1]] == 0:
                del s_count[s[i - 1]]
            s_count[s[i + np - 1]] += 1
            if s_count == p_count:
                res.append(i)

        return res
