class Solution:
    def findKthNumber(self, n, k):
        def count_prefix(prefix, n):
            # 計算以 prefix 開頭的數字個數
            cur = prefix
            next_ = prefix + 1
            cnt = 0
            while cur <= n:
                cnt += min(n + 1, next_) - cur
                cur *= 10
                next_ *= 10
            return cnt

        curr = 1
        k -= 1
        while k > 0:
            cnt = count_prefix(curr, n)
            if k >= cnt:
                k -= cnt
                curr += 1
            else:
                k -= 1
                curr *= 10
        return curr
