class Solution:
    def compress(self, chars):
        write = 0
        left = 0
        n = len(chars)

        while left < n:
            right = left
            while right < n and chars[right] == chars[left]:
                right += 1
            chars[write] = chars[left]
            write += 1
            count = right - left
            if count > 1:
                for c in str(count):
                    chars[write] = c
                    write += 1
            left = right

        return write
