from collections import defaultdict

class Solution:
    def numberOfBoomerangs(self, points):
        total = 0
        for x1, y1 in points:
            dist_count = defaultdict(int)
            for x2, y2 in points:
                if (x1, y1) != (x2, y2):
                    d = (x1 - x2) ** 2 + (y1 - y2) ** 2
                    dist_count[d] += 1
            for count in dist_count.values():
                total += count * (count - 1)
        return total
