class Solution:
    def findRadius(self, houses, heaters):
        houses.sort()
        heaters.sort()
        res = 0
        i = 0  # 指向最近的右邊加熱器

        for house in houses:
            # 找到離 house 最近的右邊加熱器
            while i < len(heaters) - 1 and abs(heaters[i+1] - house) <= abs(heaters[i] - house):
                i += 1
            # 記錄最遠距離
            res = max(res, abs(heaters[i] - house))
        return res
