class Solution:
    def licenseKeyFormatting(self, S, K):
        # 移除所有 '-' 並轉為大寫
        S = S.replace('-', '').upper()
        n = len(S)
        first_group_len = n % K or K  # 第一組長度

        res = [S[:first_group_len]]
        for i in range(first_group_len, n, K):
            res.append(S[i:i+K])
        return '-'.join(res)
