import collections 

class Solution(object):
    def findMinStep(self, board, hand):
        """
        :type board: str
        :type hand: str
        :rtype: int
        """

        # Function to remove consecutive same balls from the board
        def remove_same(s, i):
            if i < 0:
                return s

            left = right = i
            while left > 0 and s[left-1] == s[i]:
                left -= 1
            while right+1 < len(s) and s[right+1] == s[i]:
                right += 1

            length = right - left + 1
            if length >= 3:
                new_s = s[:left] + s[right+1:]
                return remove_same(new_s, left-1)  # recursively clean the new string
            else:
                return s

        # Sort hand to optimize insertion
        hand = "".join(sorted(hand))

        # BFS queue: (current board, current hand, current step)
        q = collections.deque([(board, hand, 0)])
        visited = set([(board, hand)])  # Visited states to prevent reprocessing

        while q:
            curr_board, curr_hand, step = q.popleft()

            for i in range(len(curr_board) + 1):
                for j in range(len(curr_hand)):
                    # Skip consecutive balls in hand to prevent duplicate insertions
                    if j > 0 and curr_hand[j] == curr_hand[j-1]:
                        continue

                    # Skip positions where we don't need to insert (if left side has same color)
                    if i > 0 and curr_board[i-1] == curr_hand[j]:
                        continue

                    pick = False
                    # Check if inserting the ball will help remove
                    # 1. Same color with the right ball
                    # 2. Left and right balls are the same, but current hand ball is different
                    if i < len(curr_board) and curr_board[i] == curr_hand[j]:
                        pick = True
                    if 0 < i < len(curr_board) and curr_board[i-1] == curr_board[i] and curr_board[i] != curr_hand[j]:
                        pick = True

                    if pick:
                        # Simulate the board after insertion and removal
                        new_board = remove_same(curr_board[:i] + curr_hand[j] + curr_board[i:], i)
                        new_hand = curr_hand[:j] + curr_hand[j+1:]  # Remove the used ball from hand

                        # If the board is empty, return the number of steps
                        if not new_board:
                            return step + 1

                        # If the new state is not visited, process it
                        if (new_board, new_hand) not in visited:
                            q.append((new_board, new_hand, step + 1))
                            visited.add((new_board, new_hand))

        # If no solution was found
        return -1
