class Solution(object):
    def groupAnagrams(self, strs):
        """
        :type strs: List[str]
        :rtype: List[List[str]]
        """
        from collections import defaultdict

        anagrams = defaultdict(list)
        for s in strs:
            key = tuple(sorted(s))  # 將字串排序後作為 key
            anagrams[key].append(s)

        return list(anagrams.values())
