class Solution:
    def complexNumberMultiply(self, a, b):
        def parse(s):
            real, imag = s.split('+')
            return int(real), int(imag[:-1])

        a_real, a_imag = parse(a)
        b_real, b_imag = parse(b)

        real_part = a_real * b_real - a_imag * b_imag
        imag_part = a_real * b_imag + a_imag * b_real

        return str(real_part) + '+' + str(imag_part) + 'i'
