class Solution(object):
    def shoppingOffers(self, price, special, needs):
        n = len(price)
        memo = {}

        def dfs(curr_needs):
            key = tuple(curr_needs)
            if key in memo:
                return memo[key]
            res = sum(curr_needs[i] * price[i] for i in range(n))
            for offer in special:
                temp = []
                for i in range(n):
                    if offer[i] > curr_needs[i]:
                        break
                    temp.append(curr_needs[i] - offer[i])
                else:
                    res = min(res, offer[-1] + dfs(temp))
            memo[key] = res
            return res

        return dfs(needs)
