class Solution(object):
    def numDecodings(self, s):
        MOD = 10**9 + 7
        n = len(s)
        dp = [0] * (n + 1)
        dp[0] = 1
        for i in range(1, n + 1):
            if s[i-1] == '*':
                dp[i] = 9 * dp[i-1]
            elif s[i-1] != '0':
                dp[i] = dp[i-1]
            if i > 1:
                if s[i-2] == '*' and s[i-1] == '*':
                    dp[i] += 15 * dp[i-2]
                elif s[i-2] == '*':
                    dp[i] += (2 if s[i-1] <= '6' else 1) * dp[i-2]
                elif s[i-1] == '*':
                    dp[i] += (9 if s[i-2] == '1' else 6 if s[i-2] == '2' else 0) * dp[i-2]
                else:
                    if 10 <= int(s[i-2:i]) <= 26:
                        dp[i] += dp[i-2]
            dp[i] %= MOD
        return dp[n]
