class MyCircularDeque:
    def __init__(self, k):
        self.k = k
        self.buf = [0] * k
        self.head = 0
        self.count = 0

    def insertFront(self, value):
        if self.count == self.k:
            return False
        self.head = (self.head - 1) % self.k
        self.buf[self.head] = value
        self.count += 1
        return True

    def insertLast(self, value):
        if self.count == self.k:
            return False
        idx = (self.head + self.count) % self.k
        self.buf[idx] = value
        self.count += 1
        return True

    def deleteFront(self):
        if self.count == 0:
            return False
        self.head = (self.head + 1) % self.k
        self.count -= 1
        return True

    def deleteLast(self):
        if self.count == 0:
            return False
        self.count -= 1
        return True

    def getFront(self):
        if self.count == 0:
            return -1
        return self.buf[self.head]

    def getRear(self):
        if self.count == 0:
            return -1
        idx = (self.head + self.count - 1) % self.k
        return self.buf[idx]

    def isEmpty(self):
        return self.count == 0

    def isFull(self):
        return self.count == self.k
