class Solution:
    def countSubstrings(self, s):
        n = len(s)
        count = [0]  # 使用 list 避免 nonlocal

        def expand(left, right):
            while left >= 0 and right < n and s[left] == s[right]:
                count[0] += 1
                left -= 1
                right += 1

        for i in range(n):
            expand(i, i)       # odd length
            expand(i, i + 1)   # even length

        return count[0]
