class Solution(object):
    def isNumber(self, s):
        s = s.strip()
        if not s:
            return False
        num_seen = False
        dot_seen = False
        e_seen = False
        for i, char in enumerate(s):
            if char.isdigit():
                num_seen = True
            elif char == '.':
                if dot_seen or e_seen:
                    return False
                dot_seen = True
            elif char in ['e', 'E']:
                if e_seen or not num_seen:
                    return False
                e_seen = True
                num_seen = False
            elif char in ['+', '-']:
                if i != 0 and s[i-1] not in ['e', 'E']:
                    return False
            else:
                return False
        return num_seen
