class Solution:
    def strangePrinter(self, s):
        n = len(s)
        if n == 0:
            return 0
        dp = [[0] * n for _ in range(n)]

        for i in range(n-1, -1, -1):
            dp[i][i] = 1
            for j in range(i+1, n):
                dp[i][j] = 1 + dp[i+1][j]
                for k in range(i+1, j+1):
                    if s[k] == s[i]:
                        if k+1 <= j:
                            dp[i][j] = min(dp[i][j], dp[i][k-1] + dp[k+1][j])
                        else:
                            dp[i][j] = min(dp[i][j], dp[i][k-1])
        return dp[0][n-1]
