class Solution:
    def constructArray(self, n, k):
        res = []
        left, right = 1, k + 1

        # 先生成 k 個不同差值
        for i in range(k + 1):
            if i % 2 == 0:
                res.append(left)
                left += 1
            else:
                res.append(right)
                right -= 1

        # 剩下的直接遞增填充
        for i in range(k + 2, n + 1):
            res.append(i)

        return res
