class Solution:
    def checkValidString(self, s):
        low = 0  # 最少未匹配的 '(' 數
        high = 0 # 最多未匹配的 '(' 數

        for c in s:
            if c == '(':
                low += 1
                high += 1
            elif c == ')':
                if low > 0:
                    low -= 1
                high -= 1
            else:  # '*'
                if low > 0:
                    low -= 1
                high += 1
            if high < 0:
                return False
        return low == 0
