class Solution(object):
    def minStickers(self, stickers, target):
        from collections import Counter

        m = len(stickers)
        sticker_count = [Counter(s) for s in stickers]
        memo = {}

        def dp(t):
            if t == "":
                return 0
            if t in memo:
                return memo[t]

            t_count = Counter(t)
            ans = float('inf')
            for s in sticker_count:
                if t[0] not in s:
                    continue
                new_t = ''.join([c * max(0, t_count[c] - s.get(c, 0)) for c in t_count])
                tmp = dp(new_t)
                if tmp != -1:
                    ans = min(ans, 1 + tmp)
            memo[t] = -1 if ans == float('inf') else ans
            return memo[t]

        return dp(target)
