from collections import defaultdict

class Solution:
    def accountsMerge(self, accounts):
        parent = {}

        def find(x):
            parent.setdefault(x, x)
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        email_to_name = {}
        for acc in accounts:
            name = acc[0]
            first_email = acc[1]
            rest = acc[2:]
            for email in [first_email] + rest:
                parent_email = find(first_email)
                parent_email2 = find(email)
                parent[parent_email2] = parent_email
                email_to_name[email] = name

        merged = defaultdict(list)
        for email in email_to_name:
            root = find(email)
            merged[root].append(email)

        return [[email_to_name[root]] + sorted(emails) for root, emails in merged.items()]
