class Solution(object):
    def countOfAtoms(self, formula):
        from collections import Counter
        n = len(formula)
        self.i = 0

        def parse():
            cnt = Counter()
            while self.i < n and formula[self.i] != ')':
                if formula[self.i] == '(':
                    self.i += 1
                    inner = parse()
                    self.i += 1  # skip ')'
                    mult = 0
                    while self.i < n and formula[self.i].isdigit():
                        mult = mult * 10 + int(formula[self.i])
                        self.i += 1
                    if mult == 0:
                        mult = 1
                    for k, v in inner.items():
                        cnt[k] += v * mult
                else:
                    start = self.i
                    self.i += 1
                    while self.i < n and formula[self.i].islower():
                        self.i += 1
                    name = formula[start:self.i]
                    num = 0
                    while self.i < n and formula[self.i].isdigit():
                        num = num * 10 + int(formula[self.i])
                        self.i += 1
                    if num == 0:
                        num = 1
                    cnt[name] += num
            return cnt

        counts = parse()
        parts = []
        for atom in sorted(counts.keys()):
            parts.append(atom + (str(counts[atom]) if counts[atom] > 1 else ""))
        return "".join(parts)
