class Solution(object):
    def selfDividingNumbers(self, left, right):
        res = []
        for num in range(left, right + 1):
            temp = num
            divisible = True
            while temp > 0:
                digit = temp % 10
                if digit == 0 or num % digit != 0:
                    divisible = False
                    break
                temp //= 10
            if divisible:
                res.append(num)
        return res
