class Solution:
    def evaluate(self, expression):
        def eval_expr(expr, env):
            if expr[0] != '(':
                if expr[0].isalpha():
                    return env[expr]
                return int(expr)
            expr = expr[1:-1]
            tokens = []
            balance = 0
            start = 0
            for i, c in enumerate(expr):
                if c == '(':
                    balance += 1
                elif c == ')':
                    balance -= 1
                elif c == ' ' and balance == 0:
                    tokens.append(expr[start:i])
                    start = i + 1
            tokens.append(expr[start:])
            op = tokens[0]
            if op == 'add':
                return eval_expr(tokens[1], env.copy()) + eval_expr(tokens[2], env.copy())
            elif op == 'mult':
                return eval_expr(tokens[1], env.copy()) * eval_expr(tokens[2], env.copy())
            elif op == 'let':
                new_env = env.copy()
                i = 1
                while i < len(tokens) - 1:
                    var = tokens[i]
                    val = eval_expr(tokens[i + 1], new_env)
                    new_env[var] = val
                    i += 2
                return eval_expr(tokens[-1], new_env)
        return eval_expr(expression, {})
